function exist(elem) {
  (elem.length>0) ? true : false;
}
function randd(min, max) {
  return Math.floor(arguments.length > 1 ? (max - min + 1) * Math.random() + min : (min + 1) * Math.random());
};
// перемешивание
function shuffle(o){
    if (o.length == undefined || typeof o != 'object')
      return [0];
    for(var j, x, k = o.length; k; j = Math.floor(Math.random() * k), x = o[--k], o[k] = o[j], o[j] = x);
    return o;
};

function getViewPortSize(mod)
{
    var viewportwidth;
    var viewportheight;

    //Standards compliant browsers (mozilla/netscape/opera/IE7)
    if (typeof window.innerWidth != 'undefined')
    {
        viewportwidth = window.innerWidth,
        viewportheight = window.innerHeight
    }

    // IE6
    else if (typeof document.documentElement != 'undefined'
    && typeof document.documentElement.clientWidth !=
    'undefined' && document.documentElement.clientWidth != 0)
    {
        viewportwidth = document.documentElement.clientWidth,
        viewportheight = document.documentElement.clientHeight
    }

    //Older IE
    else
    {
        viewportwidth = document.getElementsByTagName('body')[0].clientWidth,
        viewportheight = document.getElementsByTagName('body')[0].clientHeight
    }

	if(mod=="width")
		return viewportwidth;
	
    return viewportwidth + "~" + viewportheight;
}

function makeComboBox(src) {
	var ret = '';
	var arrow="<div class='combo_box_arrow'>"+ARR_UP+"</div>";
	for (var i in src.l) {
		var type = src.l[i];
		if (src.l[i].list.length < 2) {
		  var subtype = type.list[0];
		  ret+="<input type='checkbox' value='"+type.name+" "+subtype.name+"' id='ch_"+subtype.name+"'><label for='ch_"+subtype.name+"' title='"+subtype.tooltip+"' data-bg='"+type.bg+"' data-hierarchy='root'>"+subtype.title+"</label>";
		} else {
		  ret+= "<input type='checkbox' value='"+type.name+"' id='ch_"+type.name+"' data-root='"+type.name+"'><label for='ch_"+type.name+"' data-bg='"+type.bg+"' data-hierarchy='root'>"+type.title+"</label>";
		  for(var j in type.list) {
			var subtype = type.list[j];
			ret+= "<input type='checkbox' value='"+subtype.name+"' id='ch_"+subtype.name+"' data-parent='"+type.name+"'><label for='ch_"+subtype.name+"' title='"+subtype.tooltip+"' data-bg='"+type.bg+"' data-hierarchy='child'>"+subtype.title+"</label>";
		  }
		}
	}
	ret = "<div id='selector' class='combo_box' data-text='Выберите список'><div class='combo_box_title'>Выберите список</div><div class='combo_box_content'>"+ret+"</div>"+arrow+"</div>";
	//$("body").html(ret);
	return ret;
}

